import { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { restaurantAPI } from '../services/api';

const Restaurants = () => {
  const [restaurants, setRestaurants] = useState([]);
  const [allRestaurants, setAllRestaurants] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchQuery, setSearchQuery] = useState('');
  const [activeFilter, setActiveFilter] = useState('All');
  const [selectedDepartment, setSelectedDepartment] = useState('');
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    fetchRestaurants();
  }, []);

  useEffect(() => {
    // Handle navigation from Home page with filter
    if (location.state?.filterType) {
      handleFilterClick(location.state.filterType);
    }
  }, [location.state, allRestaurants]);

  const fetchRestaurants = async () => {
    try {
      const response = await restaurantAPI.getAll();
      setRestaurants(response.data.data);
      setAllRestaurants(response.data.data);
    } catch (error) {
      console.error('Error fetching restaurants:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = async () => {
    if (!searchQuery.trim()) {
      setRestaurants(allRestaurants);
      setActiveFilter('All');
      setSelectedDepartment('');
      return;
    }
    try {
      const response = await restaurantAPI.search(searchQuery);
      setRestaurants(response.data.data);
      setActiveFilter('All');
      setSelectedDepartment('');
    } catch (error) {
      console.error('Error searching restaurants:', error);
    }
  };

  const handleFilterClick = (filterType) => {
    setActiveFilter(filterType);
    setSelectedDepartment('');
    setSearchQuery('');
    
    let filteredRestaurants = [...allRestaurants];
    
    switch (filterType) {
      case 'All':
        setRestaurants(allRestaurants);
        break;
      case 'Canteens':
        filteredRestaurants = allRestaurants.filter(r => 
          r.cuisine?.toLowerCase().includes('canteen') || 
          r.name?.toLowerCase().includes('canteen')
        );
        setRestaurants(filteredRestaurants);
        break;
      case 'Food Stalls':
        filteredRestaurants = allRestaurants.filter(r => 
          r.cuisine?.toLowerCase().includes('stall') || 
          r.name?.toLowerCase().includes('stall')
        );
        setRestaurants(filteredRestaurants);
        break;
      case 'Coffee Shops':
        filteredRestaurants = allRestaurants.filter(r => 
          r.cuisine?.toLowerCase().includes('coffee') || 
          r.name?.toLowerCase().includes('coffee') ||
          r.cuisine?.toLowerCase().includes('cafe')
        );
        setRestaurants(filteredRestaurants);
        break;
      case 'Top Rated':
        filteredRestaurants = allRestaurants.filter(r => r.rating >= 4.0);
        setRestaurants(filteredRestaurants);
        break;
      default:
        setRestaurants(allRestaurants);
    }
  };

  const handleDepartmentClick = (department) => {
    setSelectedDepartment(department);
    setActiveFilter('All');
    setSearchQuery('');
    
    // Filter restaurants by department (this would ideally come from backend)
    const departmentRestaurants = allRestaurants.filter(r => {
      // For demo purposes, we'll simulate department filtering
      const restaurantId = r.id;
      switch (department) {
        case 'CS Department':
          return restaurantId % 5 === 0;
        case 'Engineering':
          return restaurantId % 3 === 0;
        case 'Business':
          return restaurantId % 7 === 0;
        case 'Medical':
          return restaurantId % 4 === 0;
        case 'Central Campus':
          return restaurantId % 2 === 0;
        default:
          return true;
      }
    });
    
    setRestaurants(departmentRestaurants);
  };

  const handleRestaurantClick = (restaurantId) => {
    navigate(`/restaurant/${restaurantId}`);
  };

  if (loading) {
    return <div className="loading">Loading campus restaurants...</div>;
  }

  return (
    <div className="restaurants-page">
      <div className="container">
        <div className="page-header">
          <h1>Campus Food Partners</h1>
          <p>Discover amazing food from campus canteens, cafeterias, and food stalls</p>
        </div>

        {/* Search and Filters */}
        <div className="search-section">
          <div className="search-bar">
            <input 
              type="text" 
              placeholder="Search canteens, food stalls, or cuisines..." 
              className="search-input"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && handleSearch()}
            />
            <button className="search-btn" onClick={handleSearch}>üîç</button>
            {(searchQuery || activeFilter !== 'All' || selectedDepartment) && (
              <button 
                className="clear-btn"
                onClick={() => {
                  setSearchQuery('');
                  setActiveFilter('All');
                  setSelectedDepartment('');
                  setRestaurants(allRestaurants);
                }}
                title="Clear all filters"
              >
                ‚úï
              </button>
            )}
          </div>
          <div className="filters">
            <button 
              className={`filter-btn ${activeFilter === 'All' ? 'active' : ''}`}
              onClick={() => handleFilterClick('All')}
            >
              All
            </button>
            <button 
              className={`filter-btn ${activeFilter === 'Canteens' ? 'active' : ''}`}
              onClick={() => handleFilterClick('Canteens')}
            >
              Canteens
            </button>
            <button 
              className={`filter-btn ${activeFilter === 'Food Stalls' ? 'active' : ''}`}
              onClick={() => handleFilterClick('Food Stalls')}
            >
              Food Stalls
            </button>
            <button 
              className={`filter-btn ${activeFilter === 'Coffee Shops' ? 'active' : ''}`}
              onClick={() => handleFilterClick('Coffee Shops')}
            >
              Coffee Shops
            </button>
            <button 
              className={`filter-btn ${activeFilter === 'Top Rated' ? 'active' : ''}`}
              onClick={() => handleFilterClick('Top Rated')}
            >
              Top Rated
            </button>
          </div>
          {(activeFilter !== 'All' || selectedDepartment) && (
            <div className="active-filters">
              <span className="filter-label">Active filters:</span>
              {activeFilter !== 'All' && (
                <span className="active-filter-tag">
                  {activeFilter}
                  <button onClick={() => handleFilterClick('All')}>√ó</button>
                </span>
              )}
              {selectedDepartment && (
                <span className="active-filter-tag">
                  {selectedDepartment}
                  <button onClick={() => {
                    setSelectedDepartment('');
                    setRestaurants(allRestaurants);
                  }}>√ó</button>
                </span>
              )}
            </div>
          )}
        </div>

        {/* Featured Restaurants */}
        <section className="featured-section">
          <h2>Featured Campus Vendors</h2>
          <div className="restaurants-grid">
            {restaurants.filter(r => r.featured).map(restaurant => (
              <div key={restaurant.id} className="restaurant-card featured" onClick={() => handleRestaurantClick(restaurant.id)}>
                <div className="restaurant-image">
                  <span className="restaurant-emoji">{restaurant.imageUrl}</span>
                  <div className="featured-badge">Featured</div>
                </div>
                <div className="restaurant-info">
                  <h3>{restaurant.name}</h3>
                  <p className="cuisine">{restaurant.cuisine}</p>
                  <div className="restaurant-meta">
                    <span className="rating">‚≠ê {restaurant.rating}</span>
                    <span className="delivery-time">üïí {restaurant.deliveryTime}</span>
                  </div>
                  <div className="location-badge">
                    <span className="location-icon">üìç</span>
                    <span>Campus Location</span>
                  </div>
                  <button className="order-btn">View Menu</button>
                </div>
              </div>
            ))}
          </div>
        </section>

        {/* Department Categories */}
        <section className="department-section">
          <h2>Browse by Department</h2>
          <div className="department-grid">
            <div 
              className={`department-card ${selectedDepartment === 'CS Department' ? 'active' : ''}`}
              onClick={() => handleDepartmentClick('CS Department')}
            >
              <div className="dept-icon">üíª</div>
              <h3>CS Department</h3>
              <p>3 vendors</p>
            </div>
            <div 
              className={`department-card ${selectedDepartment === 'Engineering' ? 'active' : ''}`}
              onClick={() => handleDepartmentClick('Engineering')}
            >
              <div className="dept-icon">‚ö°</div>
              <h3>Engineering</h3>
              <p>5 vendors</p>
            </div>
            <div 
              className={`department-card ${selectedDepartment === 'Business' ? 'active' : ''}`}
              onClick={() => handleDepartmentClick('Business')}
            >
              <div className="dept-icon">üè¢</div>
              <h3>Business</h3>
              <p>2 vendors</p>
            </div>
            <div 
              className={`department-card ${selectedDepartment === 'Medical' ? 'active' : ''}`}
              onClick={() => handleDepartmentClick('Medical')}
            >
              <div className="dept-icon">üè•</div>
              <h3>Medical</h3>
              <p>4 vendors</p>
            </div>
            <div 
              className={`department-card ${selectedDepartment === 'Central Campus' ? 'active' : ''}`}
              onClick={() => handleDepartmentClick('Central Campus')}
            >
              <div className="dept-icon">üèõÔ∏è</div>
              <h3>Central Campus</h3>
              <p>8 vendors</p>
            </div>
          </div>
        </section>

        {/* All Restaurants */}
        <section className="all-restaurants">
          <h2>
            {selectedDepartment ? `${selectedDepartment} Vendors` : 
             activeFilter !== 'All' ? `${activeFilter}` : 'All Campus Vendors'}
            {restaurants.length > 0 && (
              <span className="results-count"> ({restaurants.length} found)</span>
            )}
          </h2>
          {restaurants.length === 0 ? (
            <div className="no-results">
              <div className="no-results-icon">üîç</div>
              <h3>No vendors found</h3>
              <p>Try adjusting your search or filter criteria</p>
              <button 
                className="btn btn-primary"
                onClick={() => {
                  setActiveFilter('All');
                  setSelectedDepartment('');
                  setSearchQuery('');
                  setRestaurants(allRestaurants);
                }}
              >
                Show All Vendors
              </button>
            </div>
          ) : (
            <div className="restaurants-grid">
              {restaurants.map(restaurant => (
                <div key={restaurant.id} className="restaurant-card" onClick={() => handleRestaurantClick(restaurant.id)}>
                  <div className="restaurant-image">
                    <span className="restaurant-emoji">{restaurant.imageUrl}</span>
                  </div>
                  <div className="restaurant-info">
                    <h3>{restaurant.name}</h3>
                    <p className="cuisine">{restaurant.cuisine}</p>
                    <div className="restaurant-meta">
                      <span className="rating">‚≠ê {restaurant.rating}</span>
                      <span className="delivery-time">üïí {restaurant.deliveryTime}</span>
                    </div>
                    <div className="location-badge">
                      <span className="location-icon">üìç</span>
                      <span>Campus Location</span>
                    </div>
                    <button className="order-btn">View Menu</button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </section>
      </div>
    </div>
  );
};

export default Restaurants;
